import React, { useEffect, useState } from "react";
import { CodeSnippet } from "../components/code-snippet";
import { PageLayout } from "../components/page-layout";
import { getProtectedResource } from "../services/message.service";
import AnteriorGradeTable from "../components/gradesheet.js";


export const GradeSheet = () => {
  const [message, setMessage] = useState("");

  useEffect(() => {
    let isMounted = true;

    const getMessage = async () => {
      const { data, error } = await getProtectedResource();

      if (!isMounted) {
        return;
      }

      if (data) {
        setMessage(JSON.stringify(data, null, 2));
      }

      if (error) {
        setMessage(JSON.stringify(error, null, 2));
      }
    };

    getMessage();

    return () => {
      isMounted = false;
    };
  }, []);


  return (
    <PageLayout>
      <div className="content-layout">
        <h1 id="page-title" className="content__title">
          Grade Sheet
        </h1>
        <div className="content__body">
          <p id="page-description">
            <span>
              This page retrieves the grade sheet of a student from the VR Nursing application.
            </span>
            <span>
              <strong>Only authenticated users should access this page.</strong>
            </span>
          </p>
          <GradeTable />
        </div>
      </div>
    </PageLayout>
  );
};
